import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-dbz-main-page',
  templateUrl: './main-page.component.html'
})

export class MainPageComponent implements OnInit {
  constructor() { }

  ngOnInit() { }
}
<h1>Ddz Personajes</h1>

<hr>

<div class="row">
  <div class="col">
    <app-dbz-list></app-dbz-list>
  </div>
  <div class="col">
    <h4>Agregar personaje</h4>
    <form action="" class="row">
      <input class="form-control mb-2" type="text" placeholder="Nombre del personaje">
      <input class="form-control mb-2" type="number" placeholder="Poder">
      <button type="submit" class="btn btn-primary">Enviar</button>
    </form>
  </div>
</div>
