import { Component, OnInit } from '@angular/core';
import { Character } from '../interfaces/character.interface';

@Component({
  selector: 'app-dbz-main-page',
  templateUrl: './main-page.component.html',
})
export class MainPageComponent {
  public characters: Character[] = [
    {
      name: 'Krillin',
      power: 1000,
    },
    {
      name: 'Gokú',
      power: 5000,
    },
    {
      name: 'Vegeta',
      power: 6000,
    },
    {
      name: 'Maestro Roshi',
      power: 50000,
    }];

    onNewCharacterMainPage(character:Character):void{
      this.characters.push(character);
    }

    onDeleteMain(index:number):void{
      console.log('onDeleteMain');
      console.log(index);

    }

}
<h1>Ddz Personajes</h1>

<hr>

<div class="row">
  <div class="col">
    <!-- [] Llaves cuadradas se usan para enviar atributos -->
    <dbz-list [characterList]="characters"
              (onDeleteCharacterListComponent)="onDeleteMain($event)"></dbz-list>
  </div>
  <div class="col">
    <!-- () Paréntesis  se usan para enviar eventos -->
    <dbz-add-character (onNewCharacterHijo)="onNewCharacterMainPage($event)" ></dbz-add-character>
  </div>
</div>
