import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Character } from '../../interfaces/character.interface';

@Component({
  selector: 'dbz-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})

export class ListComponent {
  @Input()
  public characterList: Character[] = [
    {
      name: 'Por defecto',
      power: 0
    }
  ];

  @Output()
  public onDelete: EventEmitter<number> = new EventEmitter();

  onDeleteCharacterListComponent(index:number):void{
    console.log({index});

    this.onDelete.emit(index);

  }


}
<div class="col">
  <h4>Listado</h4>
  <ul class="list-group">
    <li
      *ngFor="
        let character of characterList;
        let i = index;
        let isFirst = first;
        let isLast = last;
        let isEven = even;
        let isOdd = odd
      "
      class="list-group-item d-flex justify-content-between align-items-center"
      [ngClass]="{
        'list-group-item-dark': isLast,
        'list-group-item-primary': isEven,
      }"
    >
      <div>
        <span class="text-primary">{{ i + 1 }}</span>
        <span>
          {{ character.name }} - <strong>Power {{ character.power }}</strong>
        </span>
      </div>
      <button class="btn btn-danger btn-sm" (click)="onDeleteCharacter(i)">x</button>

      <!--
    <span> Es el primero {{isFirst}}</span>
    <span> Es el último {{isLast}}</span>
    <span> Es  par {{isEven}}</span>
    <span> Es  impar {{isOdd}}</span>
  -->
    </li>
  </ul>
</div>
