import { Component, EventEmitter, Output } from '@angular/core';
import { Character } from '../../interfaces/character.interface';

@Component({
  selector: 'dbz-add-character',
  templateUrl: './add-character.component.html',
  styleUrls: ['./add-character.component.css'],
})
export class AddCharacterComponent {

  @Output()
  public onNewCharacterHijo: EventEmitter<Character> = new EventEmitter();

  // public character: Character = {
  //   name: '',
  //   power: 0,
  // };

  emitCharacter(): void {
    // debugger;
    if(this.character.name.length === 0) return;
    this.onNewCharacterHijo.emit({...this.character});
    // this.character.name = '';
   // this.character.power = 0;
    this.character = {name:'', power:0};
  }
}
<p>add-character works!</p>
<div class="col">
  <h4>Agregar personaje</h4>
  <form action="" (submit)="emitCharacter()" class="row">
    <input
      class="form-control mb-2"
      [(ngModel)]="character.name"
      type="text"
      placeholder="Nombre del personaje"
      name="name"
    />
    <input
      class="form-control mb-2"
      [(ngModel)]="character.power"
      type="number"
      placeholder="Poder"
      name="power"
    />
    <button type="submit" class="btn btn-primary">Enviar</button>
    <!-- <pre>{{character | json}}</pre> -->
  </form>
</div>
