import { Component, OnInit } from '@angular/core';
import { Character } from '../interfaces/character.interface';
import { DbzService } from '../services/dbz.service';

@Component({
  selector: 'app-dbz-main-page',
  templateUrl: './main-page.component.html',
})
export class MainPageComponent {

  constructor(
    public dbzService: DbzService
  ) {}

  get characters() : Character[]{
    return [...this.dbzService.characters];
  }

  onDeleteCharacter(id: string):void{
    this.dbzService.deleteCharacterById(id);
  }

  onNewCharacter(character: Character):void{
    this.dbzService.addCharacter(character);
  }

}
<h1>Ddz Personajes</h1>

<hr>

<div class="row">
  <div class="col">
    <!-- [] Llaves cuadradas se usan para enviar atributos -->
    <dbz-list [characterList]="characters"
              (onDeleteListComponent)="onDeleteCharacter($event)"></dbz-list>
  </div>
  <div class="col">
    <!-- () Paréntesis  se usan para enviar eventos -->
    <dbz-add-character (onNewCharacterHijo)="dbzService.onNewCharacterMainPage($event)" ></dbz-add-character>
  </div>
</div>
