import { Component } from '@angular/core';

@Component({
  selector: 'app-heroes-hero',
  templateUrl: './hero.component.html',
  styleUrls: ['./hero.component.css']
})
export class HeroComponent {

  public name: string = 'Spiderman';
  public age: number = 45;
  public showSection:boolean = false;

  getCapitalizedName():string{
    return this.name.toUpperCase();
  }

  getHeroDescription():string{
    return `${this.name} edad ${this.age}`;
  }

  changeHero():void{
    // throw "Método no implementado";
    this.name = "Luis Reyes";
  }

  changeAge():void{
    this.age = 34;
  }

}

<h1>Spiderman</h1>

<dl>
  <td>Nombre: </td>
  <dd>{{name}}</dd>

  <td>Edad: </td>
  <dd>{{age}} </dd>

  <td>Capitalizado: </td>
  <dd>{{ getCapitalizedName() }}</dd>

  <td>Descripción: </td>
  <dd>{{ getHeroDescription() }}</dd>

</dl>

<button
  *ngIf="name !== 'Spiderman'"
  class="btn btn-primary mx-2" (click)="changeHero()">
  Cambiar nombre
</button>
<button class="btn btn-primary" (click)="changeAge()">
  Cambiar edad
</button>
