import { Component } from '@angular/core';

@Component({
  selector: 'app-heroes-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent {

  public heroNames:string[] = ['Spiderman','Ironman','Methodman'];
  public deletedHero?:string;
  // public deletedHero:string = '';

  removeLastHero():void{
    this.deletedHero = this.heroNames.pop();
  }

}
<p>Listado de héroes!</p>

<div>
  <h3 *ngIf="deletedHero; else nothingWasDeleted">Héroe eliminado: <small class="text-danger">{{ deletedHero }}</small></h3>
</div>

<ng-template #nothingWasDeleted>
  <h4 *ngIf="!deletedHero">No ha borrado nada</h4>
</ng-template>

<button *ngIf="heroNames.length > 0" class="btn btn-outline-danger" (click)="removeLastHero()" >Borrar último héroe</button>

<ul class="mt-2 list-group">
  <li *ngFor="let hero of heroNames" class="list-group-item">
    {{hero}}
  </li>
</ul>
