@Component({
  selector: 'gifs-search-box',
  template: `
  <h5>Buscar:</h5>
  <input
  type="text"
  class="form-control"
  placeholder="Buscar gifs..."
  (keyup)="searchTag(txtTagInput.value)"
  #txtTagInput
  >
  `,
})
export class SearchBoxComponent {

  constructor() {}

  searchTag( newTag: string){
    console.log({newTag});

  }

}
