import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class GifsService {

   _tagsHistory: string[] = [];

  constructor() { }

  get tagHistory(){
    return [...this._tagsHistory];
  }

  searchTag(tag: string):void{
    if( tag.length == 0) return;
    this._tagsHistory.unshift(tag);
  }

  getTags(){
    return this._tagsHistory;
  }

}
