import { Component } from '@angular/core';
import { GifsService } from 'src/app/gifs/services/gifs.service';

@Component({
  selector: 'shared-sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.css']
})
export class SidebarComponent {

  constructor(
    private gifsService: GifsService
  ) {}

  get tags(){
    return this.gifsService.getTags();
  }

  newSearch(tag: string){
    this.gifsService.searchTag(tag);
  }

}
<div class="bg-dark border-right p-3" id="sidebar">
  <h3 class="text-light">GifsApp</h3>
  <hr class="text-white">

  <div class="list-group list-reset" *ngFor="let tag of tags">
    <button class="list-group-item list-group-item-action" (click)="newSearch(tag)">{{tag | titlecase}}</button>
  </div>
</div>
