import { Component } from '@angular/core';
import { CountriesService } from '../../countries.service';
import { Country } from '../../interfaces/country';

@Component({
  selector: 'app-by-capital-page',
  templateUrl: './by-capital-page.component.html',
  styles: [
  ]
})
export class ByCapitalPageComponent {

  countries: Country[] = [];

  constructor(
    private countriesService: CountriesService
  ){}

  searchByCapital(term: string){
    this.countriesService.searchCapital(term).subscribe( (countries) => {
      console.log(countries);
      this.countries = countries;
    },
    error => {
      console.log('No se obtuvieron capitales');
    })
  }

}
<h2>Por Capital</h2>
<hr>
<div class="row">
  <div class="col">
    Búsquedas
   <shared-search-box placeholder="Buscar por capital"
    (onValue)="searchByCapital($event)"
   ></shared-search-box>
   <!-- (onDeleteListComponent)="onDeleteCharacter($event)" -->
  </div>
</div>

<div class="row">
  <div class="col">
    <hr>
    <ul>
      <li *ngFor="let country of countries">
        {{country.name.nativeName.est}} - {{country.population}}
      </li>
    </ul>

  </div>
</div>
