import { Component, Input } from '@angular/core';
import { Country } from '../../interfaces/country';

@Component({
  selector: 'countries-table',
  templateUrl: './country-table.component.html',
  styles: [
    `img {
      width: 30px;
    }`
  ]
})
export class CountryTableComponent {

  @Input()
  public countries: Country[] = [];


}
<div *ngIf="countries.length === 0; else table" class="alert alert-warning">No hay pasises que mostrar</div>

<ng-template #table>
  <table class="table table-hover">
    <thead>
      <tr>
        <th>#</th>
        <th>Icon</th>
        <th>Bandera</th>
        <th>Nombre</th>
        <th>Población</th>
        <th></th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let country of countries; let i = index" >
        <td>{{ i + 1}}</td>
        <td>{{country.flag}}</td>
        <td><img src="{{country.flags.svg}}" [alt]="country.name.common"></td>
        <td>{{country.name.common}}</td>
        <td>{{country.population | number}}</td>
        <td><a [routerLink]="['/countries/by', country.cca3]">Info</a></td>
      </tr>
    </tbody>
  </table>
</ng-template>
