import { Component } from '@angular/core';
import { CountriesService } from '../../countries.service';
import { Country } from '../../interfaces/country';

@Component({
  selector: 'app-by-country-page',
  templateUrl: './by-country-page.component.html',
  styles: [
  ]
})
export class ByCountryPageComponent {

  countries: Country[] = [];
  public country: string = '';

  constructor(private countriesService: CountriesService) {}

  ngOnInit(){
    this.countries = this.countriesService.cacheStore.byCapital.countries;
    this.country = this.countriesService.cacheStore.byCapital.term;
  }

  searchByCountry(term: string) {
    this.countriesService.searchCountry(term).subscribe((countries) => {
      this.countries = countries;
      this.countriesService.cacheStore.byCountries.countries = countries;
      this.countriesService.cacheStore.byCountries.term = term;
    });
  }


}
<h2>Por País</h2>
<hr>
<div class="row">
  <div class="col">
    Búsquedas
   <shared-search-box
    placeholder="Buscar por país"
    (onDebounce)="searchByCountry($event)"
    [initialValue]="initialValue"
   ></shared-search-box>
   <!-- (onDeleteListComponent)="onDeleteCharacter($event)" -->
  </div>
</div>

<div class="row">
  <div class="col">
    <hr>
    <countries-table [countries]="countries"></countries-table>
  </div>
</div>
