import { Component, OnInit } from '@angular/core';
import { CountriesService } from '../../countries.service';
import { Country } from '../../interfaces/country';

@Component({
  selector: 'app-by-capital-page',
  templateUrl: './by-capital-page.component.html',
  styles: [],
})
export class ByCapitalPageComponent implements OnInit {
  countries: Country[] = [];
  initialValue: string  = '';
  isLoading: boolean = false;

  constructor(private countriesService: CountriesService) {}

  ngOnInit(){
    this.countries = this.countriesService.cacheStore.byCapital.countries;
    this.initialValue = this.countriesService.cacheStore.byCapital.term;
  }

  searchByCapital(term: string) {
    this.isLoading = true;
    this.countriesService.searchCapital(term).subscribe((countries) => {
      this.countries = countries;
      this.initialValue = term
      this.isLoading = false;
    });
  }
}
<h2>Por Capital</h2>
<hr>
<div class="row">
  <div class="col">
    Búsquedas
   <shared-search-box placeholder="Buscar por capital"
    (onDebounce)="searchByCapital($event)"
   ></shared-search-box>
   <!-- (onDeleteListComponent)="onDeleteCharacter($event)" -->
  </div>
</div>

<div class="row">
  <div class="col">
    <hr>
    <shared-loading-spinner *ngIf="isLoading"></shared-loading-spinner>
    <countries-table [countries]="countries"></countries-table>
  </div>
</div>
