import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';

const rtx5090 = {
  name: 'rtx5090',
  price: 2500,
  inStorage: 6
}

@Component({
  templateUrl: './basic-page.component.html',
  styles: [
  ]
})
export class BasicPageComponent implements OnInit {

  // public myForm: FormGroup = new FormGroup({
  //   name: new FormControl(''),
  //   price: new FormControl(0),
  //   inStorage: new FormControl(0),
  // })

  ngOnInit(): void {
    // this.myForm.reset(rtx5090);
  }

  isValidField(field: string): boolean {
    return Boolean(this.myForm.controls[field].errors) && this.myForm.controls[field].touched;
  }

  getFieldError(field: string): string | null {
    if (!this.myForm.controls[field]) return null;

    const errors = this.myForm.controls[field].errors || {};

    for (const key of Object.keys(errors)) {
      switch (key) {
        case 'required':
          return 'Este campo es requerido';
        case 'minlength':
          return `Mínimo ${errors['minlength'].requiredLength} carácteres.`;
      }
    }
    return null;
  }

  public myForm: FormGroup = this.fb.group({
    name: ['', [Validators.required, Validators.minLength(3)]],
    price: [0, [Validators.required, Validators.minLength(0)]],
    inStorage: [0, [Validators.required, Validators.minLength(0)]],
  })

  constructor(private fb: FormBuilder) { }

  onSave(): void {

    if (this.myForm.invalid) {
      this.myForm.markAllAsTouched();
      return
    };
    console.log(this.myForm.value);

    this.myForm.reset({ price: 10, inStorage: 0 })

  }

}
<h2>Reactivos: Básicos</h2>
<hr />

<div class="row">
  <div class="col">
    <form [formGroup]="myForm" autocomplete="off" (ngSubmit)="onSave()">
      <!-- Campo de producto -->
      <div class="mb-3 row">
        <label class="col-sm-3 col-form-label">Producto</label>
        <div class="col-sm-9">
          <input
            type="text"
            class="form-control"
            placeholder="Nombre del producto"
            formControlName="name"
          />

          <!-- PRIMERA OPCIÓN -->
          <!-- <span
            *ngIf="
              myForm.controls['name'].getError('required') &&
              myForm.controls['name'].touched
            "
            class="form-text text-danger"
            >Este campo es requerido</span
          >
          <span
            *ngIf="
              myForm.controls['name'].getError('minlength') &&
              myForm.controls['name'].touched
            "
            class="form-text text-danger"
            >Este campo requiere 3 letras</span
          > -->

          <!-- SEGUNDA OPCIÓN -->
          <span *ngIf="isValidField('name')" class="form-text text-danger">{{
            getFieldError("name")
          }}</span>
          <span *ngIf="isValidField('name')" class="form-text text-danger">{{
            getFieldError("price")
          }}</span>
        </div>
      </div>

      <!-- Campo de producto -->
      <div class="mb-3 row">
        <label class="col-sm-3 col-form-label">Precio</label>
        <div class="col-sm-9">
          <input
            type="number"
            class="form-control"
            placeholder="Precio del producto"
            formControlName="price"
          />
          <span *ngIf="isValidField('price')" class="form-text text-danger">
            El precio debe de ser 0 o mayor
          </span>
        </div>
      </div>

      <!-- Campo de existencias -->
      <div class="mb-3 row">
        <label class="col-sm-3 col-form-label">Existencias</label>
        <div class="col-sm-9">
          <input
            type="number"
            class="form-control"
            placeholder="Existencias del producto"
            formControlName="inStorage"
          />

          <span class="form-text text-danger">
            Las existencias deben de ser 0 o mayor
          </span>
        </div>
      </div>

      <div class="row">
        <div class="col">
          <button type="submit" class="btn btn-primary float-end">
            Guardar
          </button>
        </div>
      </div>
    </form>
  </div>
</div>

<div class="row">
  <div class="col">
    <span>Valid</span>
    <pre>{{ myForm.valid | json }}</pre>

    <span>Pristine</span>
    <pre>{{ myForm.pristine | json }}</pre>

    <span>Touched</span>
    <pre>{{ myForm.touched | json }}</pre>

    <span>Value</span>
    <pre>{{ myForm.value | json }}</pre>

    <span>Precio</span>
    <pre>{{ myForm.controls["price"].value | json }}</pre>

    <span>Precio Errors</span>
    <pre>{{ myForm.controls["price"].errors | json }}</pre>

    <span>Producto</span>
    <pre>{{ myForm.controls["name"].value | json }}</pre>

    <span>Producto Errors name</span>
    <pre>{{ myForm.controls["name"].errors | json }}</pre>
  </div>
</div>
