import { Component } from '@angular/core';
import { FormControl } from '@angular/forms';
import { Hero } from '../../interfaces/hero.interface';
import { HeroesService } from '../../services/heroes.service';
import { MatAutocompleteSelectedEvent } from '@angular/material/autocomplete';

@Component({
  selector: 'app-search-page',
  templateUrl: './search-page.component.html',
  styles: [
  ]
})
export class SearchPageComponent {


  public searchInput = new FormControl('');

  public heroes: Hero[] = [];

  public selectedHero?: Hero;

  constructor( private heroesService: HeroesService) {}

  searchHero(){
    const value: string = this.searchInput.value || '';
    console.log({value});
    this.heroesService.getSuggestions( value )
      .subscribe( heroes => {
        console.log(heroes);
        this.heroes = heroes;

      })
  }

  onSelectedOption( event: MatAutocompleteSelectedEvent ): void{
    console.log(event.option.value);
    if(!event.option.value){
      this.selectedHero = undefined;
      return;
    }
    const hero: Hero = event.option.value;
    this.searchInput.setValue(hero.superhero);
    this.selectedHero = hero;

  }
}
<div class="flex flex-column">
  <h3>Buscador</h3>
  <mat-form-field>
    <mat-label>Buscador de héroes</mat-label>
    <input
      type="text"
      matInput
      [formControl]="searchInput"
      (input)="searchHero()"
      [matAutocomplete]="auto"
      >
      <mat-autocomplete autoActiveFirstOption
        #auto="matAutocomplete"
        (optionSelected)="onSelectedOption( $event)"
        >
        <mat-option *ngFor="let heroe of heroes" [value]="heroe">
          {{heroe.superhero}}
        </mat-option>
        <mat-option
          *ngIf="heroes.length === 0 && searchInput.value && searchInput.value.length > 0"
        >
          No se encontto nada con el término {{ searchInput.value }}
        </mat-option>

      </mat-autocomplete>
  </mat-form-field>

  {{ selectedHero | json }}
</div>
