import { Component, Input, OnInit } from '@angular/core';
import { Hero } from '../../interfaces/hero.interface';

@Component({
  selector: 'heroes-hero-card',
  templateUrl: './card.component.html'
})
export class CardComponent implements OnInit {
  @Input()
  public hero!: Hero

  ngOnInit(){
    if(!this.hero) throw Error('Hero property is required')
  }
}
<p>card works!</p>

<mat-card>
  <mat-card-header>
    <mat-card-title>{{hero.superhero}}</mat-card-title>
    <mat-card-subtitle>{{hero.alter_ego}}</mat-card-subtitle>
  </mat-card-header>

  <mat-card-content class="mt-2">
    <h4>{{ hero.publisher }}</h4>
    <p>
      <strong>Primera Aparación</strong>{{ hero.first_appearance}}
      <br>
      <mat-chip-listbox>
        <mat-chip *ngFor="let name of hero.characters.split(',') | slice: 0:3">
          {{ name}}
        </mat-chip>
      </mat-chip-listbox>
    </p>
  </mat-card-content>
</mat-card>
