import { ConfirmDialogComponent } from './../../component/confirm-dialog/confirm-dialog.component';
import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Hero, Publisher } from '../../interfaces/hero.interface';
import { HeroesService } from '../../services/heroes.service';
import { ActivatedRoute, Router } from '@angular/router';
import { switchMap } from 'rxjs';
import { MatSnackBar } from '@angular/material/snack-bar';
import { MatDialog } from '@angular/material/dialog';


@Component({
  selector: 'app-new-page',
  templateUrl: './new-page.component.html',
  styles: [
  ]
})
export class NewPageComponent implements OnInit{

  constructor(
    private heroesService: HeroesService,
    private activatedRoute: ActivatedRoute,
    private router: Router,
    private snackBar: MatSnackBar,
    private dialog: MatDialog
    ) {}

  ngOnInit(): void {
    if( !this.router.url.includes('edit')) return;

    this.activatedRoute.params
      .pipe(
        // switchMap( params)
        switchMap( ({ id }) => this.heroesService.getHeroeById( id ) ),
      ).subscribe( hero => {
        if(!hero ) return this.router.navigateByUrl('/')
        this.heroForm.reset( hero );
        return;
      })
  }

  public heroForm = new FormGroup({
    id: new FormControl<string>(''),
    superhero: new FormControl<string>('', { nonNullable: true }),
    publisher: new FormControl<Publisher>(Publisher.DCComics),
    alter_ego: new FormControl(''),
    first_appearance: new FormControl(''),
    characters: new FormControl(''),
    alt_img: new FormControl(''),
  });

  public publishers = [
    { id: 'DC Comics', desc: 'DC-Comics'},
    { id: 'Marvel Comics', desc: 'Marvel - Comics'}
  ];

  get currentHero(): Hero{
    const hero = this.heroForm.value as Hero;
    return hero;
  }

  onSubmit():void{
    if(this.heroForm.invalid) return;

    if(this.currentHero.id){
      this.heroesService.updateHero( this.currentHero)
        .subscribe( hero => {
          this.showSnackBar(`${hero.superhero} updated!`)
          // TODO mostrar snackbar
        });
        return;
    }

    this.heroesService.addHero( this.currentHero)
      .subscribe( hero => {
        this.router.navigate(['/heroes/edit', hero.id])
        this.showSnackBar(`${hero.superhero} created!`)

        // TODO  snackbar y navegar a /heroes/edit/hero.id
      })

    console.log({
      formIsValid: this.heroForm.valid,
      value: this.heroForm.getRawValue()
    });
  }

  onDeleteHero(){
    if(!this.currentHero.id) throw Error('Hero id required');

    const dialogRef = this.dialog.open(ConfirmDialogComponent, {
      data: this.heroForm.value,
    });

    dialogRef.afterClosed().subscribe(result => {
      console.log('The dialog was closed');
      console.log({ result });

    });


  }

  showSnackBar( message: string ):void{
    this.snackBar.open( message, 'done', {
      duration: 2500
    })

  }

}
<h1>
  {{ currentHero.id ? 'Editar' : 'Agregar'}} héroe
  <small *ngIf="currentHero.id" >{{ currentHero.superhero }}</small>
</h1>

<mat-divider class="mb-2"></mat-divider>

<form class="grid" [formGroup]="heroForm" (ngSubmit)="onSubmit()">
  <div class="col-12 sm:col-6">
    <mat-card>
      <mat-card-content>
        <div class="grid">
          <mat-form-field class="col-12 sm:col-6">
            <mat-label>Súper Héroe</mat-label>
            <input
              matInput
              type="text"
              required
              formControlName="superhero"
              />
          </mat-form-field>
          <mat-form-field class="col-12 sm:col-6">
            <mat-label>Alter ego</mat-label>
            <input
              matInput
              type="text"
              required
              formControlName="alter_ego"
              />
          </mat-form-field>

          <mat-form-field class="col-12 sm:col-6">
            <mat-label>Primera aparición</mat-label>
            <input
              matInput
              type="text"
              required
              formControlName="first_appearance"
              />
          </mat-form-field>

          <mat-form-field class="col-12 sm:col-6">
            <mat-label>Personajes</mat-label>
            <input
              matInput
              type="text"
              required
              formControlName="characters"
              />
          </mat-form-field>

          <mat-form-field class="col-12 sm:col-6">
            <mat-label>Creador</mat-label>
            <mat-select
            formControlName="publisher"
            required
            >
              <mat-option
                *ngFor="let publisher of publishers"
                [value]="publisher.id"
                >{{ publisher.desc }}</mat-option
              >
            </mat-select>
          </mat-form-field>

          <mat-form-field class="col-12 sm:col-6">
            <mat-label>Alternative Image</mat-label>
            <input
              matInput
              type="text"
              required
              formControlName="alt_img"
              />
          </mat-form-field>

        </div>
        <div class="flex justify-content-between">
          <button
          *ngIf="currentHero.id"
            mat-flat-button
            color="warn"
          >Borrar</button>
          <span></span>
          <span class="spacer"></span>
          <button
          mat-flat-button
          color="primary"
          (click)="onSubmit()"
        >Guardar</button>
        </div>

      </mat-card-content>
    </mat-card>
  </div>

  <div class="col-12 sm:col-6">
    <mat-card>
      <img [src]="currentHero | heroImage" alt="imagen del héroe" mat-card-image>
    </mat-card>
  </div>
</form>

<pre>
{{ currentHero | json }}
</pre>
