import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Hero, Publisher } from '../../interfaces/hero.interface';
import { HeroesService } from '../../services/heroes.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-new-page',
  templateUrl: './new-page.component.html',
  styles: [
  ]
})
export class NewPageComponent implements OnInit{

  constructor(
    private heroesService: HeroesService,
    private activatedRoute: ActivatedRoute,
    private router: Router,
    ) {}

  ngOnInit(): void {
    if(this.activatedRoute.url.incl)
  }

  public heroForm = new FormGroup({
    id: new FormControl<string>(''),
    superhero: new FormControl<string>('', { nonNullable: true }),
    publisher: new FormControl<Publisher>(Publisher.DCComics),
    alter_ego: new FormControl(''),
    first_appearance: new FormControl(''),
    characters: new FormControl(''),
    alt_img: new FormControl(''),
  });

  public publishers = [
    { id: 'DC Comics', desc: 'DC-Comics'},
    { id: 'Marvel Comics', desc: 'Marvel - Comics'}
  ];

  get currentHero(): Hero{
    const hero = this.heroForm.value as Hero;
    return hero;
  }

  onSubmit():void{
    if(this.heroForm.invalid) return;

    if(this.currentHero.id){
      this.heroesService.updateHero( this.currentHero)
        .subscribe( hero => {
          // TODO mostrar snackbar
        });
        return;
    }

    this.heroesService.addHero( this.currentHero)
      .subscribe( hero => {
        // TODO  snackbar y navegar a /heroes/edit/hero.id
      })

    console.log({
      formIsValid: this.heroForm.valid,
      value: this.heroForm.getRawValue()
    });
  }

}
<h1>
  Editar / Agregar héroe
  <small>Súperman</small>
</h1>

<mat-divider class="mb-2"></mat-divider>

<form class="grid" [formGroup]="heroForm" (ngSubmit)="onSubmit()">
  <div class="col-12 sm:col-6">
    <mat-card>
      <mat-card-content>
        <div class="grid">
          <mat-form-field class="col-12 sm:col-6">
            <mat-label>Súper Héroe</mat-label>
            <input
              matInput
              type="text"
              required
              formControlName="superhero"
              />
          </mat-form-field>
          <mat-form-field class="col-12 sm:col-6">
            <mat-label>Alter ego</mat-label>
            <input
              matInput
              type="text"
              required
              formControlName="alter_ego"
              />
          </mat-form-field>

          <mat-form-field class="col-12 sm:col-6">
            <mat-label>Primera aparición</mat-label>
            <input
              matInput
              type="text"
              required
              formControlName="first_appearance"
              />
          </mat-form-field>

          <mat-form-field class="col-12 sm:col-6">
            <mat-label>Personajes</mat-label>
            <input
              matInput
              type="text"
              required
              formControlName="characters"
              />
          </mat-form-field>

          <mat-form-field class="col-12 sm:col-6">
            <mat-label>Creador</mat-label>
            <mat-select
            formControlName="publisher"
            required
            >
              <mat-option
                *ngFor="let publisher of publishers"
                [value]="publisher.id"
                >{{ publisher.desc }}</mat-option
              >
            </mat-select>
          </mat-form-field>

          <mat-form-field class="col-12 sm:col-6">
            <mat-label>Alternative Image</mat-label>
            <input
              matInput
              type="text"
              required
              formControlName="alt_img"
              />
          </mat-form-field>

        </div>
        <div class="flex justify-content-between">
          <button
            mat-flat-button
            color="warn"
          >Borrar</button>
          <span class="spacer"></span>
          <button
          mat-flat-button
          color="primary"
          (click)="onSubmit()"
        >Guardar</button>
        </div>

      </mat-card-content>
    </mat-card>
  </div>

  <div class="col-12 sm:col-6">
    <mat-card>
      <img [src]="currentHero | heroImage" alt="imagen del héroe" mat-card-image>
    </mat-card>
  </div>
</form>

<pre>
{{ currentHero | json }}
</pre>
