import { Component } from '@angular/core';
import { AuthService } from '../../services/auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login-pages',
  templateUrl: './login-pages.component.html',
  styles: [
  ]
})
export class LoginPagesComponent {

  constructor(
    private authService: AuthService,
    private router: Router
    ) {}

  onLogin(): void{
    this.authService.login('lreyes@chek.cl','12345')
      .subscribe( user => {
        this.router.navigate(['/'])
      })
  }

}
<div class="flex flex-column">
  <span class="text-lg mb-4">Login</span>
    <mat-form-field>
      <mat-label>Usuario</mat-label>
      <input type="text" matInput placeholder="Nombre de usuario">
    </mat-form-field>
    <mat-form-field>
      <mat-label>Contraseña</mat-label>
      <input type="text" matInput placeholder="Contraseña">
    </mat-form-field>

    <button
      mat-button
      mat-flat-button
      color="primary"
      (click)="onLogin()"
      >
      <mat-icon>save</mat-icon>
      Ingresar
    </button>

    <div class="flex justify-content-end mt-5">
      <a routerLink="/auth/new-account">¿Ya tienes cuenta?</a>
    </div>


  </div>
