import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { CountriesService } from '../../services/countries.service';
import { Region, SmallCountry } from '../../interfaces/country.interface';
import { filter, switchMap, tap } from 'rxjs';

@Component({
  selector: 'app-selector-page',
  templateUrl: './selector-page.component.html',
  styles: [
  ]
})
export class SelectorPageComponent implements OnInit {

  public countriesByRegion: SmallCountry[] = [];
  public borders: SmallCountry[] = [];

  public myForm: FormGroup = this.fb.group({
    region: ['', [Validators.required]],
    country: ['', [Validators.required]],
    border: ['', [Validators.required]],
  })

  constructor(
    private fb: FormBuilder,
    private countriesService: CountriesService
  ) { }

  ngOnInit(): void {
    this.onRegionChanged();
    this.onCountryChanged();
  }

  get regions(): Region[] {
    return this.countriesService.regions;
  }

  onRegionChanged(): void {
    this.myForm.get('region')!.valueChanges
      .pipe(
        tap(() => this.myForm.get('country')!.setValue('')),
        tap(() => this.borders = []),
        switchMap(region => this.countriesService.getCountriesByRegion(region))
      )
      .subscribe(countries => {
        this.countriesByRegion = countries;
        console.log({ countries });
      })
  }

  onCountryChanged(): void {

    this.myForm.get('country')!.valueChanges
      .pipe(
        tap(() => this.myForm.get('border')!.setValue('')),
        // filter -> si se cumple la condición sigue con la solicitud
        filter((value: string) => value.length > 0),
        switchMap(alphaCode => this.countriesService.getCountryByAlphaCode(alphaCode)),
        switchMap(country => this.countriesService.getCountryBordersByCodes(country.borders))
      )
      .subscribe(countries => {
        this.borders = countries;
        console.log({ borders: this.borders });

      })
  }


}
<h1>Selectores anidados</h1>
<hr />
<form [formGroup]="myForm" action="">
  <!-- Region -->
  <div class="row mb-3">
    <div class="col">
      <label class="form-label">Continente:</label>
      <select class="form-control" formControlName="region">
        <option value="">Seleccione continente</option>
        <option *ngFor="let region of regions" [value]="region">
          {{ region }}
        </option>
      </select>
    </div>
  </div>

  <!-- Countries -->
  <div class="row mb-3" *ngIf="countriesByRegion.length > 0">
    <div class="col">
      <label class="form-label">País:</label>
      <select class="form-control" formControlName="country">
        <option value="">Seleccione país</option>
        <option
          *ngFor="let country of countriesByRegion"
          [value]="country.cca3"
        >
          {{ country.name }}
        </option>
      </select>
    </div>
  </div>

  <!-- Fronteras -->
  <div class="row mb-3" *ngIf="borders.length > 0">
    <div class="col">
      <label class="form-label">Fronteras:</label>
      <select class="form-control" formControlName="border">
        <option value="">Seleccione frontera</option>
        <option *ngFor="let border of borders" [value]="border">
          {{ border }}
        </option>
      </select>
    </div>
  </div>
</form>

<h3>Formulario</h3>
<code>
  {{ myForm.value | json }}
</code>
<p>is form valid: {{ myForm.valid | json }}</p>
